import React, { Component } from "react";
import Newsitems from "./Newsitems";

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles:[],
    };
  }
  async componentDidMount() {
    console.log("cdm");
    let url =
      "https://newsapi.org/v2/top-headlines?country=in&apikey=dbe57b028aeb41e285a226a94865f7a7";
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({ articles: parsedData.articles });
 }
  render() {
    return (
      <>
        <div className="container">
          <h2>This is Hedder</h2>
          <div className="row">
            {this.state.articles.map((element) => {
              return (
                <div className="col-md-4" key={element.url}>
                  <Newsitems
                    title={element.title ? element.title.slice(0, 45) : "title null"}
                    description={
                      element.description
                        ? element.description.slice(0, 88)
                        : "description null"
                    }
                    imgeurl={element.urlToImage?element.urlToImage:"https://image.cnbcfm.com/api/v1/image/107138943-1666373971994-gettyimages-1417565288-coachtrip0011131.jpeg?v=1666379188&w=1920&h=1080"}
                    newsurl={element.url}
                  />
                </div>
              );
            })}
<button type="button" class="btn btn-dark">Previous</button>
<button type="button" class="btn btn-dark">Next</button>
          </div>
        </div>
      </>
    );
  }
}
export default News;
