import React, { Component } from "react";
import Newsitems from "./Newsitems";
import Spinner from "./Spinner";
import PropTypes from 'prop-types'

export class News extends Component {
  
  static defaultProps={
    category:'general',
    country:"us"
  }
  static propTypes={
    country:PropTypes.string,
    category:PropTypes.string,
  }
  constructor() {
    super();
    this.state = {
      articles: [],
      page: 1,
      loading: false,
    };
  }
  updateIt = async() => {

    
    this.setState({ loading: true });
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apikey=dbe57b028aeb41e285a226a94865f7a7&page=${
      this.state.page}&pageSize=9`;
    let data = await fetch(url);
    let parsedData = await data.json();

    this.setState({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading: false,
    });
  }

  async componentDidMount() {
    this.setState({ loading: true });
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apikey=dbe57b028aeb41e285a226a94865f7a7&page=${
      this.state.page}&pageSize=9`;
    let data = await fetch(url);
    let parsedData = await data.json();

    this.setState({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading: false,
    });
  }

  clickNext = async () => {
    // this.setState({ loading: true });
    // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apikey=dbe57b028aeb41e285a226a94865f7a7&page=${
    //   this.state.page + 1
    // }&pageSize=9`;
    // let data = await fetch(url);
    // let parsedData = await data.json();

    this.setState({
      page: this.state.page + 1,
      // articles: parsedData.articles,
      // loading: false,
    });
    this.updateIt()
  };

  clickPrev = async () => {
    // this.setState({ loading: true });
    // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apikey=dbe57b028aeb41e285a226a94865f7a7&page=${
    //   this.state.page - 1
    // }&pageSize=9`;
    // let data = await fetch(url);
    // let parsedData = await data.json();

    this.setState({
      page: this.state.page - 1,
      // articles: parsedData.articles,
      // loading: false,
    });
    this.updateIt()
  };

  render() {
    return (
      <>
        <div className="container">
          <div className="row">
            {this.state.loading ? (
              <Spinner />
            ) : (
              this.state.articles.map((element) => {
                return (
                  <div className="col-md-4" key={element.url}>
                    <Newsitems
                      title={
                        element.title
                          ? element.title.slice(0, 45)
                          : "title null"
                      }
                      description={
                        element.description
                          ? element.description.slice(0, 88)
                          : "description null"
                      }
                      imgeurl={
                        element.urlToImage
                          ? element.urlToImage
                          : "https://image.cnbcfm.com/api/v1/image/107138943-1666373971994-gettyimages-1417565288-coachtrip0011131.jpeg?v=1666379188&w=1920&h=1080"
                      }
                      newsurl={element.url}
                      author={element.author}
                      date={element.publishedAt}
                      source_id={element.source.id?element.source.id:"Unknown"}
                      source_name={element.source.name}
                    />
                  </div>
                );
              })
            )}
            <div className="container d-flex justify-content-between m-3">
              <button
                disabled={this.state.page <= 1}
                onClick={this.clickPrev}
                type="button"
                className="btn btn-dark"
              >
                &larr; Previous
              </button>
              <button
                disabled={
                  Math.ceil(this.state.totalResults / 9) < this.state.page + 1
                }
                type="button"
                onClick={this.clickNext}
                className="btn btn-dark"
              >
                Next &rarr;
              </button>
            </div>
          </div>
        </div>
      </>
    );
  }
}
export default News;
