      page: page+1
    })
  }
  
  clickPrev =() =>{
  
  }

  render() {
    return (
      <>
        <div className="container">
          <h2>This is Hedder</h2>
          <div className="row">
            {this.state.articles.map((element) => {
              return (
                <div className="col-md-4" key={element.url}>
                  <Newsitems
                    title={
                      element.title ? element.title.slice(0, 45) : "title null"
                    }
                    description={
                      element.description
                        ? element.description.slice(0, 88)
                        : "description null"
                    }
                    imgeurl={
                      element.urlToImage
                        ? element.urlToImage
                        : "https://image.cnbcfm.com/api/v1/image/107138943-1666373971994-gettyimages-1417565288-coachtrip0011131.jpeg?v=1666379188&w=1920&h=1080"
                    }
                    newsurl={element.url}
                  />
                </div>
              );
            })}
            <div className="container d-flex justify-content-between m-3">
              <button onClick={this.clickPrev} type="button" class="btn btn-dark">
                &rarr; Previous
              </button>
              <button type="button" onClick={this.clickNext} class="btn btn-dark">
                Next &rarr;
              </button>
            </div>
          </div>
        </div>
      </>
    );
  }
}
export default News;
