import React, { Component } from "react";
import Newsitems from "./Newsitems";

export class News extends Component {

async componentDidMount() {
  let url =
  "https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=cbd3f0d8059846778287beca5f9883c8";
  let data = await fetch(url);
  let parseData = await data.json();
  this.setState({ articles: parseData.articles });
}
constructor() {
  super();
  this.state = {
    articles: this.articles,
    loading: false,
  }
};
  render() {
    return (
      <>
        <div className="container">
          <h2>This is Hedder</h2>
          <div className="row">
            {this.state.articles.map((e) => {
              return (
                <div key={e.url} className="col-md-3">
                  <Newsitems
                    newsurl={e.url}
                    title={e.title.slice(0, 45)}
                    imgeurl={e.urlToImage}
                    discription={e.description.slice(0, 80)}
                  />
                </div>
              );
            })}
          </div>
        </div>
      </>
    );
  }
}
export default News;
